import React from "react"



class AddUser extends React.Component {
    constructor(props){
        super(props)
        this.state = {
            firstname: "",
            lastname: "",
            bio: "",
            age: 1,
            isHappy: false
        }
    }
    render(){
      return (
      <form ref={(el)=> this.myFrom = el}>
        <input placeholder="Имя" onChange={(e)=> this.setState({firstname: e.target.value})}  />
        <input placeholder="Фамилия" onChange={(e)=> this.setState({lastname: e.target.value})}/>
        <textarea placeholder="Биография" onChange={(e)=> this.setState({bio: e.target.value})}/>
        <input placeholder="Возраст" onChange={(e)=> this.setState({age: e.target.value})}/>
        <label htmlFor="isHapy">Счастлив?</label>
        <input type="checkbox" id="isHapy" onChange={(e)=> this.setState({isHapy: e.target.checked  })}/>
        <button type="button" onClick={()=> {
           this
        this.props.onAdd({
          firstname: this.state.firstname,
          lastname: this.state.lastname,
            bio: this.state.bio,
            age: this.state.age,
            isHappy: this.state.isHapy,
    })}
    }>Добавить</button>
      </form>
      )
  }


  }

  export default AddUser
