import React from "react";
import Header from "./components/header";
import Users from "./components/Users";
import AddUser from "./components/AddUser";

class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
          users: [
            {
              id: 1,
              firstname: "Bob",
              lastname: "Marley",
              bio: "great singer",
              age: 40,
              isHappy: true,
            },
            {
              id: 2,
              firstname: "Jhon",
              lastname: "Doe",
              bio: "artist",
              age: 22,
              isHappy: false,
            },
          ],
        };
      }
  render() {
    return (
      <div>
        <Header title="Список пользователей" />
        <main>
          <Users users = {this.state.users} />
        </main>
        <aside>
            <AddUser/>
        </aside>
      </div>
    );
  }
}

export default App;
